import React, { useEffect, useState } from "react";
import { db } from "../firebase";
import { doc, getDoc } from "firebase/firestore";
import { getAuth } from "firebase/auth";

export default function MemberPanel({ serverId }) {
  const [members, setMembers] = useState([]);
  const [owner, setOwner] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!serverId) return;
    setLoading(true);
    const fetchMembers = async () => {
      const serverSnap = await getDoc(doc(db, "servers", serverId));
      if (!serverSnap.exists()) {
        setMembers([]);
        setOwner(null);
        setLoading(false);
        return;
      }
      const data = serverSnap.data();
      setOwner(data.owner);
      setMembers(data.members || []);
      setLoading(false);
    };
    fetchMembers();
  }, [serverId]);

  // Pour afficher l'email, on utilise l'API Auth (limité au user courant, sinon on affiche l'uid)
  const auth = getAuth();
  const currentUser = auth.currentUser;

  if (!serverId) return <div className="w-56 bg-gray-800 h-screen p-4 border-l border-gray-900"></div>;

  return (
    <div className="w-56 bg-gray-800 h-screen p-4 border-l border-gray-900 flex flex-col">
      <h3 className="text-lg font-bold mb-4">Membres</h3>
      {loading ? (
        <div className="text-purple-200">Chargement...</div>
      ) : (
        <ul className="space-y-2">
          {owner && (
            <li className="bg-indigo-700 rounded px-3 py-2 text-white font-semibold flex items-center gap-2">
              <span className="text-xs bg-indigo-900 px-2 py-1 rounded">Owner</span>
              {currentUser && currentUser.uid === owner ? currentUser.email : owner}
            </li>
          )}
          {members.filter(uid => uid !== owner).map(uid => (
            <li key={uid} className="bg-gray-900 rounded px-3 py-2 text-purple-200 flex items-center">
              {currentUser && currentUser.uid === uid ? currentUser.email : uid}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
