import React, { useEffect, useState } from "react";
import { auth, db } from "../firebase";
import { collection, query, where, onSnapshot } from "firebase/firestore";

export default function DMList({ selectedDM, onSelect }) {
  const user = auth.currentUser;
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;
    const q = query(
      collection(db, "privateConversations"),
      where("members", "array-contains", user.uid)
    );
    const unsub = onSnapshot(q, (snap) => {
      setConversations(snap.docs.map(doc => ({ id: doc.id, ...doc.data() })));
      setLoading(false);
    });
    return () => unsub();
  }, [user]);

  if (!user) return null;

  return (
    <div className="w-56 bg-gray-800 h-screen p-4 border-r border-gray-900 flex flex-col">
      <h3 className="text-lg font-bold mb-4">Messages priv√©s</h3>
      {loading ? (
        <div className="text-purple-200">Chargement...</div>
      ) : conversations.length === 0 ? (
        <div className="text-purple-300">Aucune conversation.</div>
      ) : (
        <ul className="space-y-2">
          {conversations.map(conv => {
            const otherUid = conv.members.find(uid => uid !== user.uid);
            return (
              <li key={conv.id}>
                <button
                  onClick={() => onSelect(conv.id)}
                  className={`w-full text-left px-3 py-2 rounded flex items-center gap-2 transition text-sm font-medium ${selectedDM === conv.id ? 'bg-purple-700 text-white' : 'bg-gray-900 hover:bg-purple-800 text-purple-200'}`}
                >
                  <span className="font-semibold">DM avec {otherUid}</span>
                </button>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
